mapper.select:
SELECT user_id, term_id, is_agreed
  FROM UserAgreement
 WHERE user_id = #{user_id}

SELECT is_agreed
  FROM UserAgreement
 WHERE user_id = #{user_id}
   AND term_id = #{term_id}

mapper.update:
UPDATE UserAgreement
   SET is_agreed = #{is_agreed}
 WHERE user_id = #{user_id}
   AND term_id = #{term_id}

mapper.delete:
DELETE FROM UserAgreement
 WHERE user_id = #{user_id}
   AND term_id = #{term_id}
