mapper.select:
SELECT user_id
FROM userInfo
WHERE user_name = #{user_name}

SELECT user_name
  FROM userInfo
 WHERE name = #{name}
   AND email = #{email}

SELECT user_password
  FROM userInfo
 WHERE user_name = #{user_name}

SELECT user_password
  FROM userInfo
 WHERE user_name = #{user_name}
   AND email = #{email}

SELECT COUNT(*)
FROM userInfo
WHERE user_name = #{user_name}

SELECT COUNT(*)
FROM userInfo
WHERE name = #{name}

SELECT COUNT(*)
FROM userInfo
WHERE email = #{email}

SELECT COUNT(*)
FROM userInfo
WHERE nickname = #{nickname}
